mapper.select:
SELECT a.*, dict.value as status_value, dict.remark as status_color, su.username as sys_user_name, sd.name as sys_dept_name, u.nickname as user_name FROM t_user_balance_log a LEFT JOIN t_user_info u ON (a.user_id=u.userId) left join `sys_dict` dict on(a.status=dict.code and dict.type='user_balance_log_status') left join sys_user su on (su.user_id=u.sysUserId) left join sys_dept sd on(su.dept_id = sd.dept_id) WHERE 1 = 1
and ( u.nickname like '%${params.key}%' or a.user_id=#{params.key} or u.`searchPhone` like '%${params.key}%' )
and a.status = #{params.status}
and a.type = #{params.type}
and u.sysUserId=#{obj.sysUserId}
and su.dept_id=#{params.sysDeptId}
order by a.create_time desc

SELECT
sum(a.amount)
FROM t_user_balance_log a
LEFT JOIN t_user_info u ON (a.user_id=u.userId)
left join sys_user su on (su.user_id=u.sysUserId)
WHERE 1 = 1

    and (
    u.nickname like '%${params.key}%'
    or a.user_id=#{params.key}
    or u.`searchPhone` like '%${params.key}%'
    )


    and a.status = #{params.status}


    and a.type = #{params.type}


    and u.sysUserId=#{obj.sysUserId}


    and su.dept_id=#{params.sysDeptId}

select sum(amount) from t_user_balance_log
where user_id=#{params.userId}
  and `type`=#{params.type}
  and `status`=#{params.status}
